package hackerRank;
import java.util.*;
import java.io.*;


public class Loops_II {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner in = new Scanner(System.in);
        int t=in.nextInt();
        for(int i=0;i<t;i++){
            int a = in.nextInt();
            int b = in.nextInt();
            int n = in.nextInt();
        }
        for (int i = 0; i <= n-1; i++) {
        	System.out.println("a+2^i*b");
        }
        in.close();

	}

}
